import React, { useState } from 'react';
import CodeConverter from './components/CodeConverter';
import Debugger from './components/Debugger';
import CodeQualityChecker from './components/CodeQualityChecker';

function App() {
  const [view, setView] = useState('converter');

  return (
    <div style={{ padding: '20px' }}>
      <h1>Code Tools</h1>
      <nav style={{ marginBottom: '20px' }}>
        <button onClick={() => setView('converter')}>Code Converter</button>
        <button onClick={() => setView('debugger')}>Debugger</button>
        <button onClick={() => setView('quality')}>Code Quality Checker</button>
      </nav>

      {view === 'converter' && <CodeConverter />}
      {view === 'debugger' && <Debugger />}
      {view === 'quality' && <CodeQualityChecker />}
    </div>
  );
}

export default App;
