// src/components/Debugger.js
import React, { useState } from 'react';
import { Button, Box, TextField, Typography } from '@mui/material';
import { debugCode } from '../services/api';
import CodeDisplay from './CodeDisplay';

function Debugger() {
  const [code, setCode] = useState('');
  const [result, setResult] = useState('');

  const handleDebug = async () => {
    try {
      const response = await debugCode(code);
      setResult(response.result);
    } catch (error) {
      console.error(error);
      setResult(`Error: ${error.response?.data?.detail || error.message}`);
    }
  };

  return (
    <Box display="flex" flexDirection="column" gap={2}>
      <Typography variant="h5">Debugger</Typography>
      <TextField
        label="Paste your code here..."
        multiline
        rows={6}
        value={code}
        onChange={(e) => setCode(e.target.value)}
        variant="outlined"
      />
      <Button variant="contained" onClick={handleDebug}>
        Debug
      </Button>
      <Typography variant="h6" marginTop={2}>
        Debug Output:
      </Typography>
      {result && <CodeDisplay code={result} language="plaintext" />}
    </Box>
  );
}

export default Debugger;
